import React from "react";
import '../App.css';

const PetCard = ({ pets }) => {
  const { name, type, breed, age, color, status } = pets;

  return (
    <>
    <div className="pet-card">
      <h3>{name}</h3>
      <p>Status: {status}</p>
      <p>Type: {type}</p>
      {type === 'dog' ? <p>Breed: {breed}</p> : null}
      <p>Age: {age}</p>
      <p>Color: {color}</p>
    </div>
    </>
  )
}

export default PetCard;
