import React, { useState } from "react";
import { useQuery } from "@apollo/client";

import { QUERY_PETS } from "../utils/queries";

export default function PetCard({ petId, petName, type, breed, age, sex, furColor, status }) {
  const [isFlipped, setIsFlipped] = useState(false);

  const handleClick = () => {
    setIsFlipped(!isFlipped);
  };

  const { loading, error, data } = useQuery(QUERY_PETS);

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {error.message}</p>;
  }

  return (
    <div className={`pet-card ${isFlipped ? 'flipped' : ''}`} onClick={handleClick}>
      <div className="card-front">
        <p>{status}</p>
        {/* Add image here using pet.image or any other relevant field */}
        <div className="card-back">
          <h3>{petName}</h3>
          <p>Type: {type}</p>
          {type === 'dog' ? <p>Breed: {breed}</p> : null}
          <p>Age: {age}</p>
          <p>Sex: {sex}</p>
          <p>Color: {furColor}</p>
        </div>
      </div>
    </div>
  );
}
