import React, { useState } from "react";
import { useQuery } from "@apollo/client";

import { QUERY_PETS } from "../utils/queries";

export default function PetCard({ petName, type, breed, age, sex, furColor, status }) {
  const { loading, error, data } = useQuery(QUERY_PETS);
  const [isFlipped, setIsFlipped] = useState(false);

  const handleClick = () => {
    setIsFlipped(!isFlipped);
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {error.message}</p>;
  }

  const petData = data.pets;

  return (
    <div className={`pet-card ${isFlipped ? 'flipped' : ''}`} onClick={handleClick}>
      <div className="card-front">
      <p>{petData.status}</p>
      {/* {will need to add image here } */}
      <div className="card-back">
      <h3>{petData.petName}</h3>
        <p>Type: {petData.type}</p>
        {type === 'dog' ? <p>Breed: {petData.breed}</p> : null}
        <p>Age: {petData.age}</p>
        <p>Sex: {petData.sex}</p>
        <p>Color: {petData.furColor}</p>
      </div>
      </div>
    </div>
  );
}
